//et = (6.1078 * Math.pow(10,(7.5 * T / (T + 237.3))));

//at = (217 * et / (T + 273.15));




at = 6.1078 * Math.pow(10,((7.5 * T/(T + 237.3))));
et = at * RH / 100;
HD = (100 - RH) * at/100;
VH = 0.794 * et / (1 + 0.00366 * T);
DT = 237.3 * Math.log10((6.1078 / et))/ (Math.log10((et/6.1078))-7.5)

msg.payload = {
    T : T,
    RH : RH,
    at : at,
    et :et,
    HD : HD.toFixed(1),
    VH : VH.toFixed(1),
    DT : DT.toFixed(1)
}

return msg;

