    //para
    var Pc = 221200;　//臨界圧 [hPa]
　　var Tc = 647.3;　//臨界温度[K]
　　var x = (1 - (T + 273.15) / Tc);
　　var A = -7.76451;
　　var B = 1.45838;
　　var C = -2.7758;
　　var D = -1.23303;
    et = (Pc * Math.exp((A * x + B * Math.pow(x,1.5) + C * Math.pow(x,3) + D * Math.pow(x,6)) / (1 - x)))

    //水蒸気圧
    e = et * RH / 100;

    //飽和水蒸気量
    at = 217 * et / (T + 273.15)

    //露点温度
    //DT = Tn / ((m / (Math.pow(et / A)))-1);
    DT = 237.3 * Math.log(e / 6.11) / (7.5 * Math.log(10) + Math.log(6.11 / e))

    //絶対湿度
    VH = at * RH / 100;

    //飽差
    HD = (100 - RH) * at/100;
