var getCurrentTime = function () {
    var date = new Date();
    date.setUTCHours(date.getUTCHours() + 9); //+ 9
    var d = date.getUTCFullYear() + '-';
    d += ('0' + (date.getUTCMonth() + 1)).slice(-2) + '-';
    d += ('0' + date.getUTCDate()).slice(-2) + 'T';
    d += ('0' + date.getUTCHours()).slice(-2) + ':';
    d += ('0' + date.getUTCMinutes()).slice(-2) ;//+ ':';
    //d += ('0' + date.getSeconds()).slice(-2) + 'Z';
    return d;
};

